mapper.select:
SELECT t.*
FROM sys_org t
WHERE t.name = #{name} AND t.id != #{orgId};

SELECT t.user_total
FROM sys_org t
WHERE t.id = #{id}

SELECT t.*
FROM sys_org t
WHERE t.id = #{id}

SELECT t.id,t.name,t.org_order orgOrder,t.user_total userTotal,t.status,t.p_id pId, t.update_time updateTime , t.create_time createTime,t.create_uid createUid, t.update_uid updateUid from sys_org t
t.p_id = #{pId}
t.status = #{status}
ORDER BY t.org_order
